import React, { useState } from 'react';

const InputForm = ({ streams, districts, onSubmit, loading }) => {
        const [stream, setStream] = useState('');
        const [district, setDistrict] = useState('');
        const [zScore, setZScore] = useState('');
        const [error, setError] = useState('');

        const handleSubmit = (e) => {
            e.preventDefault();
            setError('');
            if (!stream || !district || !zScore) return;
            const z = parseFloat(zScore);
            if (isNaN(z) || z < -4 || z > 4) {
                setError('Z-score must be between -4 and +4.');
                return;
            }
            onSubmit({ stream, district, zScore: z });
        };

        return ( <
            form className = "input-form"
            onSubmit = { handleSubmit } >
            <
            div >
            <
            label > Subject Stream < /label> <
            select value = { stream }
            onChange = { e => setStream(e.target.value) }
            required >
            <
            option value = "" > Select stream < /option> {
                streams.map(s => ( <
                    option key = { s }
                    value = { s } > { s } < /option>
                ))
            } <
            /select> <
            /div> <
            div >
            <
            label > District < /label> <
            select value = { district }
            onChange = { e => setDistrict(e.target.value) }
            required >
            <
            option value = "" > Select district < /option> {
                districts.map(d => ( <
                    option key = { d }
                    value = { d } > { d.replace(/_/g, ' ') } < /option>
                ))
            } <
            /select> <
            /div> <
            div >
            <
            label > Z - score < /label> <
            input type = "number"
            step = "0.001"
            min = "-4"
            max = "4"
            value = { zScore }
            onChange = { e => setZScore(e.target.value) }
            required /
            >
            <
            /div> {
                error && < div style = {
                        { color: 'red', marginTop: '0.5rem' } } > { error } < /div>} <
                    button type = "submit"
                disabled = { loading } > { loading ? 'Loading...' : 'Get Recommendations' } < /button> <
                    /form>
            );
        };

        export default InputForm;
